package vonglap;
import java.util.Scanner;
public class SoChanNhoHonHaiMuoi {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int chan=2;
		System.out.println("Các số chẵn nhỏ hơn hoặc bằng 20:");
		while(chan<=20) {
			System.out.println(chan);
			chan+=2;
		}
	}

}
